import React, { useRef }  from 'react';
import '../index.css'
import { Link, NavLink } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {  checkIsAuth, logout } from '../redux/features/auth/authSlice'
import { toast } from 'react-toastify'
import { FaBars, FaTimes } from 'react-icons/fa'


export const Navbar = () => {

    const isAuth = useSelector(checkIsAuth)
    const dispatch = useDispatch()

    const navRef = useRef();
    const showNavbar = () => {
        navRef.current.classList.toggle('responsive_nav');
    }


    const activeStyle = {
        color: 'green'
    }

    const logoutHandler = () => {
        dispatch(logout())
        window.localStorage.removeItem('token')
        toast('Ви вийшли з системи')
    }


    return <div className='nav'>

            {isAuth && (
                <div className='nav-bar-login'>
                    <span className='logo'>
                        EManager
                    </span>
                    <nav ref={navRef}>

                            <NavLink
                                to={"/"}
                                href='/'
                                className='link'
                                style={({isActive}) => isActive ? activeStyle : undefined}
                            >Головна
                            </NavLink>

                            <NavLink
                                to={'/posts'}
                                href="/"
                                className='link'
                                style={({ isActive }) => isActive ? activeStyle : undefined}
                            >Записи</NavLink>

                            <NavLink
                                to={'new'}
                                href="/"
                                className='link'
                                style={({ isActive }) => isActive ? activeStyle : undefined}
                            >Додати запис</NavLink>

                            <NavLink
                                to={'/offers'}
                                href="/"
                                className='link'
                                style={({ isActive }) => isActive ? activeStyle : undefined}
                            >Прохання</NavLink>


                        <button className='nav-btn nav-close-btn' onClick={showNavbar}><FaTimes size={23}/></button>

                    </nav>
                    <div className='nav-block-btn'>
                        <button className='nav-btn' onClick={showNavbar}><FaBars size={25} /></button>
                        <button onClick={logoutHandler} className='btn_enter'>Вихід</button>
                    </div>
                </div>

            )}

            {!isAuth && (
                <div className='nav-bar-logout'>
                    <span className='logo'>
                        EManager
                    </span>
                    <Link to={'/login'} className='link_enter'>Вхід</Link>
                </div>
            )}



    </div>
}
