import React, { useState }  from 'react';
import '../index.css'
import { Link, NavLink } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {  checkIsAuth, logout } from '../redux/features/auth/authSlice'
import { toast } from 'react-toastify'


export const Navbar = () => {

    const isAuth = useSelector(checkIsAuth)
    const dispatch = useDispatch()

    const [my_burger, setBurgerClass] = useState('burger-bar umclicked')
    const [menu_burger, setMenuClass] = useState('menu hidden')
    const [isMenuClicked, setMenuClicked] = useState(false)


    const updateMenu = () => {
        if (!isMenuClicked) {
            setBurgerClass('burger-bar clicked')
            setMenuClass('menu visible')
        } else {
            setBurgerClass('burger-bar unclicked')
            setMenuClass('menu hidden')
        }
    }


    // const activeStyle = {
    //     color: 'green'
    // }

    const logoutHandler = () => {
        dispatch(logout())
        window.localStorage.removeItem('token')
        toast('Ви вийшли з системи')
    }


    return <div className='nav-bar'>
            <span className='logo'>
                EManager
            </span>

            {isAuth && (
        //     <div className='nav-li'>
        //     <ul className="nav-li ul-li">
        //             <li>
        //                 <NavLink
        //                     to={"/"}
        //                     href='/'
        //                     className='link'
        //                     style={({isActive}) => isActive ? activeStyle : undefined}
        //                 >Головна
        //                 </NavLink>
        //             </li>
        //             <li>
        //                 <NavLink
        //                     to={'/posts'}
        //                     href="/"
        //                     className='link'
        //                     style={({ isActive }) => isActive ? activeStyle : undefined}
        //                 >Записи</NavLink>
        //             </li>
        //             <li>
        //                 <NavLink
        //                     to={'new'}
        //                     href="/"
        //                     className='link'
        //                     style={({ isActive }) => isActive ? activeStyle : undefined}
        //                 >Додати запис</NavLink>
        //             </li>
        //              <li>
        //                 <NavLink
        //                     to={'/offers'}
        //                     href="/"
        //                     className='link'
        //                     style={({ isActive }) => isActive ? activeStyle : undefined}
        //                 >Прохання</NavLink>
        //             </li>
        //     </ul>
        // </div>
            <div style={{ width: '70%', height: 'auto'}}>
                <nav className='burgerNav'>
                    <div className='burger-menu'>
                        <div className={my_burger} onClick={updateMenu}>1</div>
                        <div className={my_burger} onClick={updateMenu}>2</div>
                        <div className={my_burger} onClick={updateMenu}>3</div>
                    </div>
                </nav>
            </div>
        )}

        {isAuth ? (
            <button onClick={logoutHandler} className='btn_enter'>Вихід</button>
        ) : (
                <Link to={'/login'} className='link_enter'>Вхід</Link>
        )}



    </div>
}
