import React, {useEffect} from 'react';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PostItem } from '../components/PostItem';
import '../index.css';
import { getAllPost } from '../redux/features/post/postSlice';
import { AiOutlineSearch } from 'react-icons/ai'



export const MainPage = () => {

    const dispatch = useDispatch()
    const { posts } = useSelector((state) => state.post)
    const { user } = useSelector((state) => state.auth) 

    const [value, setValue] = useState('')

    const filterPost = ''
    if(value) {
        const filterPost = posts.filter(post => {
            return post.model.includes(value)
        })
        }


    useEffect(() => {
         dispatch(getAllPost())
    }, [dispatch])

    if (!user) {
        return<div className='postViewMain'>Авторизуйтесь</div>
    }

    if (!posts.length) {
        return <div className='postViewMain'>Записи відсутні</div>
    }


    return (
        <div className='main-page'>
            <div className='searchBlock'><AiOutlineSearch size={30} style={{ transform: 'rotate(90deg)', marginRight: '5px' }}/><input type='text' placeholder='Вводь модель для пошуку...' onChange={(e) => setValue(e.target.value)} className='search'/></div>
            {
                user?._id && filterPost?.map((post, idx) => (
                    <PostItem key={idx} post={post} />
                ))
            }


        </div>
        )
}
