import React, { useEffect, useState } from 'react';
import { useCallback } from 'react';
import Moment from 'react-moment';
import { Link, useParams, useNavigate } from 'react-router-dom';
import axios from '../utils/axios';
import '../index.css'
import { useSelector, useDispatch } from 'react-redux';
import { AiFillDelete } from 'react-icons/ai';
import { removeOffer } from '../redux/features/offer/offerSlice';
import { toast } from 'react-toastify'
import { AiOutlineRead, AiOutlineStar, AiOutlineCheck, AiOutlineFileImage, AiOutlineEdit } from 'react-icons/ai'

export const OfferDelPage = () => {

    const [offer, setOffer] = useState(null)
    const params = useParams()
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const removeOfferHandler = () => {
        try {
            dispatch(removeOffer(params.id))
            toast('Запис видалено')
            navigate('/offers')
        } catch (error) {
            console.log(error)
        }
    }


    const {user} = useSelector((state) => state.auth)

    const fetchOffer = useCallback(async () => {
        const { data } = await axios.get(`/offers/${params.id}`)
        setOffer(data)
    }, [params.id])

    useEffect(() => {
        fetchOffer()
    }, [fetchOffer])

    if (!offer) {
        return (
            <div className='postViewMain'>
                Завантаження...
            </div>
        )
    }
    return (
        <div className='postViewMain'>
            <div>
                <button className='btn_enter'><Link className='link-item' to={'/offers'}>Назад</Link> </button>
            </div>

            <div className="postView">
                <div className="postHeader">
                    <h3 className="postTitle"><AiOutlineStar size={25} style={{ marginRight:'10px'}} />{offer.model}</h3>
                    {/* <Moment date={post.createdAt} format='D MMM YYYY' className="postData"/> */}
                </div>
                <div className="postBlock">
                    <div className="postBlockUrl"><a href={offer.FirstName} rel='noreferrer noopener' target='_blank'><AiOutlineCheck size={25} style={{ marginRight:'10px'}}/>{post.urlOff}</a></div>
                    <div className="postBlockUrl"><a href={offer.LastName} rel='noreferrer noopener' target='_blank'><AiOutlineRead size={25} style={{ marginRight:'10px'}}/>{post.urlBook}</a></div>
                </div>
            </div>       
            <div>

                    <div className='btnViewNav'>
                        <button className='BtnDelEdit' onClick={removeOfferHandler}><AiFillDelete size={20} style={{ color:'#fff'}}/></button>
                    </div>
            </div>  
        </div>
        )
}
