import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { OfferItem } from '../components/OfferItem';
import '../index.css';
import { getAllOffer } from '../redux/features/offer/offerSlice';



export const OfferPage = () => {

    const dispatch = useDispatch()
    const { offers } = useSelector((state) => state.offer)
    const { user } = useSelector((state) => state.auth) 

    useEffect(() => {
         dispatch(getAllOffer())
    }, [dispatch])

    // if (!user) {
    //     return<div className='postViewMain'>Авторизуйтесь</div>
    // }

    // if (offers.length) {
    //     return <div className='postViewMain'>Записи відсутні</div>
    // }


    return (
        <div className='main-page'>

            {
                offers?.map((offer, idx) => (
                    <OfferItem key={idx} offer={offer} />
                ))
            }


        </div>
        )
}
