import React, { useEffect, useState } from 'react';
import { useCallback } from 'react';
import Moment from 'react-moment';
import { Link, useParams, useNavigate } from 'react-router-dom';
import axios from '../utils/axios';
import '../index.css'
import { useSelector, useDispatch } from 'react-redux';
import { AiTwotoneEdit, AiFillDelete } from 'react-icons/ai';
import { removePost } from '../redux/features/post/postSlice';
import { toast } from 'react-toastify'

export const PostPage = () => {

    const [post, setPost] = useState(null)
    const params = useParams()
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const removePostHandler = () => {
        try {
            dispatch(removePost(params.id))
            toast('Запис видалено')
            navigate('/posts')
        } catch (error) {
            console.log(error)
        }
    }


    const {user} = useSelector((state) => state.auth)

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setPost(data)
    }, [params.id])

    useEffect(() => {
        fetchPost()
    }, [fetchPost])

    if (!post) {
        return (
            <div>
                Завантаження...
            </div>
        )
    }
    return (
        <div>

            <button className='btn_enter'><Link className='link-item' to={'/'}>Назад</Link> </button>
            <div>
                <div>
                    <div>
                        <div><Moment date={post.createdAt} format='D MMM YYYY'/></div>
                        <div className="post-item">
                            <h3>{post.model}</h3>
                            <p>{post.urlOff}</p>
                            <p>{post.urlBook}</p>
                            <p>URL img</p>
                            <p>{post.info}</p>
                        </div>
                    </div>
                </div>
                <div>
                    {
                        user?._id && (
                            <div>
                            <button><Link to={`/${params.id}/edit`}><AiTwotoneEdit /></Link></button>
                            <button onClick={removePostHandler}><AiFillDelete /></button>
                        </div>)
}
                </div>
                <div>Comments</div>
            </div>
        </div>

        )
}
