import React, {  useRef }  from 'react';
import '../index.css'
import { Link, NavLink } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {  checkIsAuth, logout } from '../redux/features/auth/authSlice'
import { toast } from 'react-toastify'
import { FaBars, FaTimes } from 'react-icons/fa'


export const Navbar = () => {

    const isAuth = useSelector(checkIsAuth)
    const dispatch = useDispatch()

    const navRef = useRef();
    const showNavbar = () => {
        navRef.current.classList.toggle('responsive_nav');
    }


    const activeStyle = {
        color: 'green'
    }

    const logoutHandler = () => {
        dispatch(logout())
        window.localStorage.removeItem('token')
        toast('Ви вийшли з системи')
    }


    return <div className='nav-bar'>
            <span className='logo'>
                EManager
            </span>

            {isAuth && (
        //     <div className='nav-li'>
        //     <ul className="nav-li ul-li">
        //             <li>
        //                 <NavLink
        //                     to={"/"}
        //                     href='/'
        //                     className='link'
        //                     style={({isActive}) => isActive ? activeStyle : undefined}
        //                 >Головна
        //                 </NavLink>
        //             </li>
        //             <li>
        //                 <NavLink
        //                     to={'/posts'}
        //                     href="/"
        //                     className='link'
        //                     style={({ isActive }) => isActive ? activeStyle : undefined}
        //                 >Записи</NavLink>
        //             </li>
        //             <li>
        //                 <NavLink
        //                     to={'new'}
        //                     href="/"
        //                     className='link'
        //                     style={({ isActive }) => isActive ? activeStyle : undefined}
        //                 >Додати запис</NavLink>
        //             </li>
        //              <li>
        //                 <NavLink
        //                     to={'/offers'}
        //                     href="/"
        //                     className='link'
        //                     style={({ isActive }) => isActive ? activeStyle : undefined}
        //                 >Прохання</NavLink>
        //             </li>
        //     </ul>
        // </div>
            <div className='navBoxing'>
                <nav ref={navRef}>

                         <NavLink
                             to={"/"}
                             href='/'
                             className='link'
                             style={({isActive}) => isActive ? activeStyle : undefined}
                         >Головна
                         </NavLink>

                         <NavLink
                             to={'/posts'}
                             href="/"
                             className='link'
                             style={({ isActive }) => isActive ? activeStyle : undefined}
                         >Записи</NavLink>

                         <NavLink
                             to={'new'}
                             href="/"
                             className='link'
                             style={({ isActive }) => isActive ? activeStyle : undefined}
                         >Додати запис</NavLink>

                         <NavLink
                             to={'/offers'}
                             href="/"
                             className='link'
                             style={({ isActive }) => isActive ? activeStyle : undefined}
                         >Прохання</NavLink>

                    <button className='nav-btn nav-close-btn' onClick={showNavbar}><FaTimes size={23}/></button>
                </nav>
                <button className='nav-btn' onClick={showNavbar}><FaBars size={23}/></button>
            </div>

        )}

        {isAuth ? (
            <button onClick={logoutHandler} className='btn_enter'>Вихід</button>
        ) : (
                <Link to={'/login'} className='link_enter'>Вхід</Link>
        )}



    </div>
}
