import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PostItem } from '../components/PostItem';
import '../index.css';
import { getAllPost } from '../redux/features/post/postSlice';



export const MainPage = () => {

    const dispatch = useDispatch()
    const { posts } = useSelector((state) => state.post)
    const { user } = useSelector((state) => state.auth)


    useEffect(() => {
         dispatch(getAllPost())
    }, [dispatch])

    if (!user) {
        return<div>Авторизуйтесь</div>
    }

    if (!posts.length) {
        return <div>Записи відсутні</div>
    }


    return (
        <div className='main-page'>
            <input type='text' placeholder='Search' onChange={(e) => console.log(e)}></input>

            {
                user?._id && posts?.map((post, idx) => (
                    <PostItem key={idx} post={post} />
                ))
            }


        </div>
        )
}
