import axios from '../utils/axios';
import React, { useEffect } from 'react';
import { useCallback, useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import '../index.css'
import { updatePost } from '../redux/features/post/postSlice';
import { toast } from 'react-toastify';

export const EditPostPage = () => {

    const [model, setModel] = useState('')
    const [urlOff, setOff] = useState('')
    const [urlBook, setBook] = useState('')
    const [info, setInfo] = useState('')

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const params = useParams()

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setModel(data.model)
        setInfo(data.info)
        setOff(data.urlOff)
        setBook(data.urlBook)
    }, [params.id])

    const submitHandler = () => {
        try {
            const updatedPost = new FormData()
            updatedPost.append('model', model)
            updatedPost.append('info', info)
            updatedPost.append('urlOff', urlOff)
            updatedPost.append('urlBook', urlBook)
            updatedPost.append('id', params.id)
            dispatch(updatePost(updatedPost))
            navigate('/posts')
            toast('Запис успішно оновлено')
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        fetchPost()
    }, [fetchPost])


    return (
        <form
            className='postsEdit'
            onSubmit={(e) => e.preventDefault()}
        >
            <input type='text' value={model} onChange={(e) => setModel(e.target.value)} placeholder='Модель' className=''></input>
            <input type='text' value={urlOff} onChange={(e) => setOff(e.target.value)} placeholder='URL офф сайту'></input>
            <input type='text' value={urlBook} onChange={(e) => setBook(e.target.value)} placeholder='URL посібника'></input>
            <textarea placeholder='Інформація' value={info} onChange={(e) => setInfo(e.target.value)}></textarea>
            <div className='posts-img'>IMG</div>

            <div className='posts-nav'>
                <button className='posts-btn' onClick={submitHandler}>Зберегти</button>
            </div>
        </form>

        )
}
