import React, { useState } from 'react';
import '../index.css'
import { Link, NavLink } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {  checkIsAuth, logout } from '../redux/features/auth/authSlice'
import { toast } from 'react-toastify'
import Hamburger from "./Hamburger";


export const Navbar = () => {

    const isAuth = useSelector(checkIsAuth)
    const dispatch = useDispatch()

    const [hamburgerOpen, setHamburgerOpen] = useState(false);

    const toggleHamburger = () =>{
        setHamburgerOpen(!hamburgerOpen)
    }

    const activeStyle = {
        color: 'green'
    }

    const logoutHandler = () => {
        dispatch(logout())
        window.localStorage.removeItem('token')
        toast('Ви вийшли з системи')
    }


    return <div className='nav-bar'>
            <span className='logo'>
                EManager
            </span>

            {isAuth && (
            // <div className='nav-li'>
            <div className="navigation">
            <ul className="nav-li ul-li">
                    <li>
                        <NavLink
                            to={"/"}
                            href='/'
                            className='link'
                            style={({isActive}) => isActive ? activeStyle : undefined}
                        >Головна
                        </NavLink>
                    </li>
                    <li>
                        <NavLink
                            to={'/posts'}
                            href="/"
                            className='link'
                            style={({ isActive }) => isActive ? activeStyle : undefined}
                        >Записи</NavLink>
                    </li>
                    <li>
                        <NavLink
                            to={'new'}
                            href="/"
                            className='link'
                            style={({ isActive }) => isActive ? activeStyle : undefined}
                        >Додати запис</NavLink>
                    </li>
                     <li>
                        <NavLink
                            to={'/offers'}
                            href="/"
                            className='link'
                            style={({ isActive }) => isActive ? activeStyle : undefined}
                        >Прохання</NavLink>
                    </li>
                </ul>
                <div className="hamburger" onClick={toggleHamburger}>
                        <Hamburger isOpen={hamburgerOpen}/>
                </div>
                <style >{`
                .navigation{
                    width: 100%;
                    height: 50px;
                }


                .navigation ul{
                    display:flex;
                    flex-wrap: wrap;
                    float: right;
                    margin: 0px;
                    padding: 0px;
                    overflow: hidden;
                }
                .navigation ul li{
                    list-style-type: none;
                    padding-right: 10px;
                }
                .hamburger{
                    display: none;
                    z-index: 6;
                } 
                @media (max-width: 767px){

                    .hamburger{
                        display:fixed;
                        padding-top: 10px;
                        margin-left: 10px;
                        z-index: 6;
                    }


                    .navigation ul{
                        display: ${hamburgerOpen ? 'inline' : 'none'};
                        background-color: blue;
                        height: 100vh;
                        width: 50vw;
                        margin-top: 50px;
                        position: fixed;

                    }
                }



            `}</style>


        </div>   
        )}

        {isAuth ? (
            <button onClick={logoutHandler} className='btn_enter'>Вихід</button>
        ) : (
                <Link to={'/login'} className='link_enter'>Вхід</Link>
        )}



    </div>
}
