import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import '../index.css'
import { createPost } from '../redux/features/post/postSlice';

export const AddPostPage = () => {

    const [model, setModel] = useState('')
    const [urlOff, setOff] = useState('')
    const [urlBook, setBook] = useState('')
    const [info, setInfo] = useState('')
    const [img, setImg] = useState('')
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const submitHandler = () => {
        try {
            const data = new FormData()
            data.append('model', model)
            data.append('urlOff', urlOff)
            data.append('urlBook', urlBook)
            data.append('info', info)
            data.append('img', img)

            dispatch(createPost(data))
            navigate('/')
        } catch (error){console.log(error)}
    }

    const clearFormData = () => {
        setModel('')
        setInfo('')
        setBook('')
        setOff('')
    }

    return (
        <form
            className='posts'
            onSubmit={(e) => e.preventDefault()}
        >
            <input type='text' value={model} onChange={(e) => setModel(e.target.value)} placeholder='Модель'></input>
            <input type='text' value={urlOff} onChange={(e) => setOff(e.target.value)} placeholder='URL офф сайту'></input>
            <input type='text' value={urlBook} onChange={(e) => setBook(e.target.value)} placeholder='URL посібника'></input>
            <textarea placeholder='Інформація' value={info} onChange={(e) => setInfo(e.target.value)}></textarea>
            <input type='file' onChange={(e) => setImg(e.target.files[0])} className='posts-file'></input>
            <div className='posts-img'>IMG</div>

            <div className='posts-nav'>
                <button className='posts-btn' onClick={submitHandler}>Зберегти</button>
                <button onClick={clearFormData} className='posts-btn'>Відміна</button>
            </div>
        </form>

        )
}
