import React from "react";
import Moment from 'react-moment';
import { Link, useNavigate, useParams } from "react-router-dom";
import '../index.css'
import { AiOutlineRead, AiOutlineStar, AiOutlineCheck } from 'react-icons/ai'
import { removeOffer } from '../redux/features/offer/offerSlice';
import { useDispatch } from "react-redux";


export const OfferItem = ({ offer }) => {

    const params = useParams()
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const removeOfferHandler = () => {
        try {
            dispatch(removeOffer(params.id))
            navigate('/offers')
        } catch (error) {
            console.log(error)
        }
    }


    if (!offer) {
        return (
            <div>
                Завантаження...
            </div>
        )
    } 

    return (
        //  <Link to={`/${offer._id}`} className='link-item'>
         <div className="postItem">
                <div className="postHeader">
                    <h3 className="postTitle"><AiOutlineStar size={25} style={{ marginRight:'10px'}} />{offer.model}</h3>

                </div>
                <div className="postBlock">
                    <div className="postUrl"><p><AiOutlineCheck size={25} style={{ marginRight:'10px'}}/>{offer.FirstName}</p></div>
                    <div className="postUrl"><p><AiOutlineRead size={25} style={{ marginRight:'10px'}}/>{offer.LastName}</p></div>
                </div>
                <button className='BtnDelEdit' onClick={removeOfferHandler}><AiOutlineCheck size={20} style={{ color:'#fff'}}/></button>
            </div>
        // </Link>

)}
